for cntry in cntrs:
    result = wikipedia.search(cntry)
    print(result)
    for i in result:
        try:
            page = wikipedia.page(i)
            #remove special characters from the title
            page.title = re.sub(r"[^a-zA-Z0-9]+", '_', page.title)
            if page.content:
                with open("/workspaces/codespaces-blank/dataset.py/data_files/"+page.title+".txt","w") as f:
                    f.write("URI["+page.url+"]")
                    f.write("\n")
                    f.write("= "+page.title+" = ")
                    f.write("\n")
                    f.write(page.content)
                with open("/workspaces/codespaces-blank/dataset.py/links_folder/main.json","r") as ff:
                    links = json.load(ff)
                    print(links)
                    for i in page.links:
                        if i not in links["data"]:
                            links["data"].append(i)
                    links["perfiledata"] = {page.title:page.links}
                print(links)
                with open("/workspaces/codespaces-blank/dataset.py/links_json/main.json","w") as ff:
                    json.dump(links,ff)
                print("done")
        except:
            print("error")
            print(i," cannot be downloaded")